import axios from "axios";
import { useStore } from "./index";

export const useCounter = () => {
  const { state, dispatch } = useStore();
  return {
    tripData: state.tripData,
    activeId: state.activeId,
    getTripData: () => {
      let urls = "http://detangled.in/develop/654da618-6cc8-4dc3-bac4-4688324b5f5f/events";
      console.log('state.url: ', urls);
      axios.request({
      	method: "get",
        url: urls,
        crossDomain: true
      }).then(result => {
        dispatch({ type: "updateTripData", tripData: result });
      });
    },
    updateTripData: () => {
      axios.request({
      	method: "post",
        url: `${state.url}/${state.activeId}`,
        crossDomain: true
      }).then(result => {
        dispatch({ type: "updateTripData", tripData: result });
      });
    },
    deleteTripData: () => {
      axios.request({
      	method: "delete",
        url: `${state.url}/${state.activeId}`,
        crossDomain: true
      }).then(result => {
        dispatch({ type: "updateTripData", tripData: result });
      });
    }
  };
};
