import axios from "axios";
import { useStore } from "./index";

export const useCounter = () => {
  const { state, dispatch } = useStore();
  return {
    tripData: state.tripData,
    activeId: state.activeId,
    getTripData: () => {
      axios.get(state.url).then(result => {
        dispatch({ type: "updateTripData", tripData: result });
      });
    },
    updateTripData: () => {
      axios.put(`${state.url}/${state.activeId}`).then(result => {
        dispatch({ type: "updateTripData", tripData: result });
      });
    },
    deleteTripData: () => {
      axios.delete(`${state.url}/${state.activeId}`).then(result => {
        dispatch({ type: "updateTripData", tripData: result });
      });
    }
  };
};
