import axios from "axios";
import { useStore } from "./index";

export const useCounter = () => {
  const { state, dispatch } = useStore();
  return {
    tripData: state.tripData,
    activeId: state.activeId,
    getTripData: () => {
      axios.request({
      	method: "get",
        url: state.url,
        crossDomain: true
      }).then(result => {
        dispatch({ type: "updateTripData", tripData: result });
      });
    },
    updateTripData: () => {
      axios.request({
      	method: "post",
        url: `${state.url}/${state.activeId}`,
        crossDomain: true
      }).then(result => {
        dispatch({ type: "updateTripData", tripData: result });
      });
    },
    deleteTripData: () => {
      axios.request({
      	method: "delete",
        url: `${state.url}/${state.activeId}`,
        crossDomain: true
      }).then(result => {
        dispatch({ type: "updateTripData", tripData: result });
      });
    }
  };
};
